mapper.select:
select shipId,shipName, shipStock, a.regDate from shipTBL a
where (shipName like concat ('%', #{keyword}, '%'))
order by shipId desc limit #{skip}, #{amount}

mapper.update:
UPDATE shipTBL SET shipName = #{shipName},shipPrice
= #{shipPrice},shipStock = #{shipStock},shipIntro =
#{shipIntro} WHERE shipId = #{shipId}


update shipTBL set shipName = #{shipName},	shipPrice = #{shipPrice}, shipStock = #{shipStock}, 
shipIntro = #{shipIntro},updateDate = now()
where shipId = ${shipId}
